import React, { useState } from "react";
import './ExpenseForm.css'

function ExpenseForm() {
const [enteredTitle, setEnteredTitle] = useState(enteredTitle);
const [enteredAmount, setEnteredAmount] = useState('');
const [enteredDate, setEnteredDate] = useState(enteredDate);

const inputChangeHandler = (event) => {
     setEnteredTitle(event.target.value)
}

const inputAmountHandler = (event) => {
    setEnteredAmount(event.target.value)
}

const inputDateHandler = (event) => {
    setEnteredDate(event.target.value)
}

    return(
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' onChange={inputChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onClick={inputAmountHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2022-12-31' onChange={inputDateHandler}></input>
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
                
            </div>


        </form>
    )
}

export default ExpenseForm;
